    $.fn.dataTable.Api.register('hasSummary()', function (table = null)
    {
        return this.getData('summary', table);
        // if(! table)
        //     table = this.getTableElement();

        // if(typeof table.data('summary') === 'undefined')
        //   return false;

        // return table.data('summary');
    });

    $.fn.dataTable.Api.register('hasSummaryActive()', function (table = null)
    {
        if(! table)
            table = this.getTableElement();

        if(! this.hasSummary(table))
            return false;

        return this.getData('summaryActive', table);

        // if(typeof table.data('summaryActive') === 'undefined')
        //     table.data('summaryActive', false);

        // return table.data('summaryActive');
    });

    $.fn.dataTable.Api.register('toggleSummary()', function (table = null)
    {
        if(! table)
            table = this.getTableElement();

        if(typeof table.data('summaryActive') === 'undefined')
            return table.data('summaryActive', true);

        table.data('summaryActive', ! table.data('summaryActive'));
    });

    $.fn.dataTable.Api.register('toggleShowSelected()', function (table = null)
    {
        if(! table)
            table = this.getTableElement();

        if(typeof table.data('showSelected') === 'undefined')
            return table.data('showSelected', true);

        table.data('showSelected', ! table.data('showSelected'));
    });

